import pubsub from 'pubsub-js';
import MyHeader from "./components/MyHeader";
import MyList from "./components/MyList";
import MyFooter from "./components/MyFooter";
export default {
  name: "App",
  components: {
    MyFooter: MyFooter,
    MyList: MyList,
    MyHeader: MyHeader
  },
  data: function data() {
    return {
      //捞储存在localStorage里的数据
      //一开始没有数据，展示空数组
      todos: JSON.parse(localStorage.getItem('todos')) || []
    };
  },
  methods: {
    //添加一个todo
    addTodo: function addTodo(todoObj) {
      this.todos = this.todos.filter(function (todo) {
        return todo;
      });
      this.todos.unshift(todoObj);
    },
    //勾选一个todo
    checkTodo: function checkTodo(id) {
      this.todos.forEach(function (todo) {
        if (todo.id === id) {
          //记录bug这里多写了个等于号= =
          todo.done = !todo.done;
        }
      });
    },
    //删除一个todo(就是留下来没删掉的，用过滤)
    //filter方法不改变原数组，所以要重新赋值
    deleteTodo: function deleteTodo(_, id) {
      this.todos = this.todos.filter(function (todo) {
        return todo.id !== id;
      });
    },
    //全选或取消全选
    checkAllTodo: function checkAllTodo(done) {
      this.todos.forEach(function (todo) {
        todo.done = done;
      });
    },
    //清除已完成任务
    clearAllTodo: function clearAllTodo() {
      this.todos = this.todos.filter(function (todo) {
        return !todo.done;
      });
    },
    //编辑
    editHandler: function editHandler(id) {
      this.todos.forEach(function (todo) {
        if (todo.id === id) todo.isEdit = !todo.isEdit;
      });
    },
    //
    updateTodo: function updateTodo(id, title) {
      this.todos.forEach(function (todo) {
        if (todo.id === id) todo.title = title;
      });
    }
  },
  mounted: function mounted() {
    this.$bus.$on('checkTodo', this.checkTodo);
    this.$bus.$on('editHandler', this.editHandler);
    this.$bus.$on('updateTodo', this.updateTodo); //console.log('我是app组件，收到了数据')

    this.pubId = pubsub.subscribe('deleteTodo', this.deleteTodo);
  },
  beforeDestroy: function beforeDestroy() {
    this.$bus.$off('checkTodo');
    this.$bus.$off('editHandler');
    this.$bus.$off('updateTodo');
    pubsub.unsubscribe(this.pubId);
  },
  //todos发生变化时候储存在localStorage里
  watch: {
    todos: {
      deep: true,
      handler: function handler(value) {
        localStorage.setItem('todos', JSON.stringify(value));
      }
    }
  }
};
